namespace Icinga\Module\Oidc;

use Icinga\Authentication\Auth;
use Icinga\Module\Oidc\Model\Provider;
use Icinga\Web\Url;
use ipl\Html\Html;
use ipl\Orm\Model;
use ipl\Web\Widget\Icon;

/**
 * Table widget to display a list of Providers
 */
class ProviderTable extends DataTable
{
    protected $defaultAttributes = [
        'class'            => 'usage-table common-table',
    ];

    public function createColumns()
    {
        $column = [];
        foreach ((new Provider())->getColumnDefinitions() as $column=>$options){

            if(is_array($options)) {
                $fieldtype = $options['fieldtype'] ?? "text";

                unset($options['fieldtype']);

                if ($fieldtype === "autocomplete" || $fieldtype === "text" || $fieldtype === "select" || $fieldtype === "color") {
                    $columns[$column] = $options['label']??$column;

                }elseif ($fieldtype === "checkbox"){
                    $columns[$column."_text"] = [
                        'label'  => $options['label']??$column,
                        'column' => function ($data) use ($column) {
                            return $data->{$column}?("Yes"):t("No");
                        }
                    ];
                }elseif ($fieldtype === "localDateTime" ){
                    $columns[$column."_txt"] = [
                        'label'  => $options['label']??$column,
                        'column' => function ($data) use ($column) {
                            if($data->{$column}!=null){
                                return $data->{$column}->format("c");
                            }
                            return t("not set");
                        }
                    ];
                }
            }else{
                $columns[$column] = $options;
            }
        }

        if (Auth::getInstance()->hasPermission('oidc/provider/modify')) {
            $columns['action'] = [
                'label' => mt('packagemirror', 'Action'),
                'attributes' => ['class' => 'icon-col'],
                'column' => function ($data) {
                    return $data;
                },
                'renderer' => function ($data) {
                    $div=Html::tag("div",['class'=>'action-column']);

                    $icon=  new Icon('pencil', ['title' => mt('oidc', 'Edit')]);
                    $a = Html::tag("a",['data-icinga-modal' => true, 'data-no-icinga-ajax' => true, 'class'=>'action-item','href'=> \ipl\Web\Url::fromPath('oidc/provider/edit',['id'=>$data->id])]);
                    $a->add($icon);
                    $div->add($a);#


                    return $div;
                }
            ];
        }

        return $columns;
    }


    protected function renderRow(Model $row)
    {
        $tr = parent::renderRow($row);

        return $tr;
    }
}
